create table post ( id INTEGER PRIMARY KEY NOT NULL AUTO_INCREMENT, location_id VARCHAR(225) NOT NULL, ad_id VARCHAR(225) NOT NULL, item_category_id VARCHAR (225), title VARCHAR (225) NOT NULL, price VARCHAR(225) NOT NULL, `condition` VARCHAR (225) NOT NULL, description VARCHAR(225) NOT NULL, user_id INTEGER NOT NULL, FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE);